import React, { useState, useEffect } from "react";
import CountrySwitchBtn from "../components/CountrySwitchBtn";
import Jumbotron from "../components/Jumbotron";
import CountryJumbotron from "../components/CountryJumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";

function Countries() {
  const [countries, setCountries] = useState([])
  const [country, setCountry] = useState([])
  const [formObject, setFormObject] = useState({})

  useEffect(() => {
    loadCountries()
  }, [])

  function loadCountries() {
    API.getCountries()
      .then(res => 
        setCountries(res.data)
      )
      .catch(err => console.log(err));
  };

  function switchCountry(event) {
    const eventReturn = event.target;
    console.log(eventReturn);
    API.switchCountry(eventReturn)
      .then(res => setCountry(res.data))
      .catch(err => console.log(err));
  }

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.name && formObject.author) {
      API.saveComment({
        author: formObject.author,
        synopsis: formObject.comment
      })
      //change next line eventually to loadComments()
        .then(res => loadCountries())
        .catch(err => console.log(err));
    }
  };

    return (
      <Container fluid>
<Row>
          <Col size="md-6 sm-12">
            <CountryJumbotron>
              <h1>Countries List</h1>
            </CountryJumbotron>
            {countries.length ? (
              <List>
                {countries.map(country => (
                  <ListItem key={country._name}>
                    <Link to={"/countries/" + country._name}>
                      <strong>
                        {country.name}
                      </strong>
                    </Link>
                    <CountrySwitchBtn onClick={() => switchCountry(country._name)} />
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
          <Col size="md-6">
            <Jumbotron>
              <h1>What advice would you give about visiting this culture?</h1>
            </Jumbotron>
            <form>
              <Input
                onChange={handleInputChange}
                name="author"
                placeholder="Author (required)"
              />
              <TextArea
                onChange={handleInputChange}
                name="comment"
                placeholder="Comment would go here"
              />
              <FormBtn
                disabled={!(formObject.author && formObject.title)}
                onClick={handleFormSubmit}
              >
                Submit Comment
              </FormBtn>
            </form>
          </Col>
        </Row>
      </Container>
    );
  }


export default Countries;
