import React, {useState} from "react";
import MapChart from "../components/MapChart";
import { Row, Container } from "../components/Grid";
import { Input, FormBtn } from "../components/Form";
import API from "../utils/API";

function Profile() {
    const [formObject, setFormObject] = useState({});

    function handleInputChange(event) {
        const { namey, value } = event.target;
        setFormObject({...formObject, [namey]: value})
      };

      //just for testing purposes
      function handleFormSubmit(event) {
        event.preventDefault();
        return API.demoUser( {
            username: formObject.namey,
            bio: formObject.bio,
            homeCountry: formObject.homeCountry,
            placesVisited: formObject.placesVisited,
            placesFuture: formObject.placesFuture
        })
        .then(res => 
          {
          setFormObject(res.data[0])
          }
        )
        .catch(err => console.log(err));
      };

      function editProfile() {
        API.editProfile(formObject.namey)
        .then(res => 
          {
          alert("Profile successfully updated!")
          }
        )
        .catch(err => console.log(err));
      };

    return (
      <Container fluid>
<Row>
    <h1>Map should appear below:</h1>
<div>
    <MapChart>
        <h3>The map below may be filled in with countries marked different colors</h3>
    </MapChart>
    <h4>Type in your username to see an example</h4>
    <form>
    <Input
                onChange={handleInputChange}
                name="namey"
                placeholder="Your username"
              />
                <FormBtn
                onClick={handleFormSubmit}
              >Submit</FormBtn>
              </form>
</div>
  </Row>
             <Row>
             <div><p>Your username is: </p> <Input
                onChange={handleInputChange}
                disabled={true}
                name="namey"
                placeholder={formObject.namey}
              /></div>
<div><p>Bio: </p> <Input
                onChange={handleInputChange}
                name="bio"
                placeholder={formObject.bio}
              /></div>
              <div><p>Your home country is: </p> <Input
                onChange={handleInputChange}
                name="homeCountry"
                placeholder={formObject.homeCountry}
              /></div>
  <div><p>Countries you've been to: </p> <Input
                onChange={handleInputChange}
                name="placesVisited"
                placeholder={formObject.homeCountry}
              /></div>
               <div><p>Countries you'd like to visit: </p> <Input
                onChange={handleInputChange}
                name="placesFuture"
                placeholder={formObject.placesFuture}
              /></div>
               </Row>
  <FormBtn
                     onClick={editProfile}
                    ><p>Edit then Submit</p></FormBtn>
      </Container>
    );
    };

export default Profile;
