import React, {useState} from "react";
import MapChart from "../components/MapChart";
import { Row, Container } from "../components/Grid";
import { Input, FormBtn } from "../components/Form";
import API from "../utils/API";

function Profile() {
    const [formObject, setFormObject] = useState({});

    function handleInputChange(event) {
        const { name, value } = event.target;
        setFormObject({...formObject, [name]: value})
      };

      //just for testing purposes
      function handleFormSubmit(event) {
          const { namey } = event.username;
        event.preventDefault();
        API.demoUser( {username: namey})
        .then(res => 
          {
              console.log("Looking for user..." + name + " and value " + value);
              console.log(res);
          setFormObject(res)
          return (
            <Row>
            <div><p>Your username is: </p>{formObject.username}</div>
            <div><p>Bio: </p>{formObject.bio}</div>
            <p>Your home country is: </p>{formObject.homeCountry}
            <p>Countries you've been to: </p>{formObject.placesVisited}
            <p>Countries you'd like to visit: </p>{formObject.placesFuture}
              </Row>
          );
          }
        )
        .catch(err => console.log(err));
      };

      function editProfile() {
return (
    <Container fluid>
        <h1>Edit here!</h1>
    </Container>
)
      }

    return (
      <Container fluid>
<Row>
    <h1>Map should appear below:</h1>
<div>
    <MapChart>
        <h3>The map below may be filled in with countries marked different colors</h3>
    </MapChart>
    <h4>Type in your username to see an example</h4>
    <form>
    <Input
                onChange={handleInputChange}
                name="username"
                placeholder="Your username"
              />
                <FormBtn
                disabled={!(formObject.username)}
                onClick={handleFormSubmit}
              >Submit</FormBtn>
              </form>
              <FormBtn
                     onClick={editProfile}
                    ><p>Edit your profile?</p></FormBtn>
</div>
  </Row>
      </Container>
    );
    };

export default Profile;
