import React, { useState } from "react";
import CountryJumbotron from "../components/CountryJumbotron";
import API from "../utils/API";
import { Row, Col, Container } from "../components/Grid";
import { Input, FormBtn } from "../components/Form";

function Login() {
  const [formObject, setFormObject] = useState({});

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  function handleFormCreateSubmit(event) {
    event.preventDefault();
    console.log("Form submitted!");
      return API.createUser({
        userName: formObject.createUsername,
        email: formObject.createEmail,
        password: formObject.createPassword
      })
        .then(res => console.log(res))
        .catch(err => console.log(err));
    };

  function handleFormLoginSubmit(event) {
    event.preventDefault();
    console.log("Form submitted!");
    API.login({
        userName: formObject.loginName,
        hash: formObject.loginPassword
      })
        .then(res => console.log(res))
        .catch(err => console.log(err))
    };

    const urlParams = new URLSearchParams(window.location.search);
    const info = urlParams.get('info');

    if(info) {
      const errorMessage = document.getElementById("error-message");
      errorMessage.innerText = info;
      errorMessage.style.display = "block";
    }

    return (
      <Container fluid>
  <Row>
<CountryJumbotron>
<form action="/login" method="post">
    <div className="title">
      <h3>Login</h3>
    </div>
    <div className="field">
      <label>Username:</label>
      <input type="text" name="username" />
      <br />
    </div>
    <div className="field">
      <label>Password:</label>
      <input type="password" name="password" required />
    </div>
    <div className="field">
      <input className="submit-btn" type="submit" value="Submit" required />
    </div>
    <label id="error-message"></label>
  </form>
            </CountryJumbotron>
  </Row>
            <Row>
                <Col size="md-6 sm-6">
            <form>
            <h1>Create an Account</h1>
            <Input
                onChange={handleInputChange}
                name="createUsername"
                placeholder="Username"

              />
              <Input
                onChange={handleInputChange}
                name="createEmail"
                placeholder="Email"

              />
              <Input
                onChange={handleInputChange}
                name="createPassword"
                placeholder="Password"
              />
              <FormBtn
                disabled={!(formObject.createUsername && formObject.createEmail && formObject.createPassword)}
                onClick={handleFormCreateSubmit}
              >
                Create Account
              </FormBtn>
            </form>
            </Col>
            <Col size="md-6 sm-6">
            <form>
            <h1>or Login to Account</h1>
              <Input
                onChange={handleInputChange}
                name="loginName"
                placeholder="Username"

              />
              <Input
                onChange={handleInputChange}
                name="loginPassword"
                placeholder="Password"
              />
              <FormBtn
                disabled={!(formObject.loginName && formObject.loginPassword)}
                onClick={handleFormLoginSubmit}
              >
                Login
              </FormBtn>
            </form>
            </Col>
            </Row>
      </Container>
    );
    };

export default Login;
