      }

    return (
      <Container fluid>
<Row>
    <h1>Map should appear below:</h1>
<div>
    <MapChart>
        <h3>The map below may be filled in with countries marked different colors</h3>
    </MapChart>
    <h4>Type in your username to see an example</h4>
    <form>
    <Input
                onChange={handleInputChange}
                name="namey"
                placeholder="Your username"
              />
                <FormBtn
                disabled={!(formObject.namey)}
                onClick={handleFormSubmit}
              >Submit</FormBtn>
              </form>
</div>
  </Row>
  <FormBtn
                     onClick={editProfile}
                    ><p>Edit your profile?</p></FormBtn>
      </Container>
    );
    };

export default Profile;
